import React, { useState } from 'react';

function EditMovie() {
    const [movie, setMovie] = useState({});
    const [loading, setLoading] = useState();
    const [error, setError] = useState();

    return (
        <>
            <h2>Add/Edit Movie</h2>
            <hr />

            <form method="post">
                <div className="mb-3">
                    <label for='title' className='form-label'>Title</label>
                    <input type='text' id='title' name="title" className='form-control' value={movie.title} />
                </div>

                <div className="mb-3">
                    <label for='release_date' className='form-label'>release_date</label>
                    <input type='text' id='release_date' name="release_date" className='form-control' value={movie.release_date} />
                </div>
            </form>
        </>
    );
}

export default EditMovie;
